import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import ProtectedRoute from './components/common/ProtectedRoute';
import './App.scss';

function App() {
  const [teams, setTeams] = useState([]);
  const [form, setForm] = useState({ name: '', city: '', founded: '' });

  useEffect(() => {
    fetch('/api/teams/')
      .then(res => res.json())
      .then(data => setTeams(data))
      .catch(console.error);
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await fetch('/api/teams/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: form.name,
          city: form.city,
          founded: parseInt(form.founded, 10)
        })
      });
      setForm({ name: '', city: '', founded: '' });
      const res = await fetch('/api/teams/');
      setTeams(await res.json());
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={
          <div className="app-container">
            <h1>Gestión de Equipos</h1>

            <form className="team-form" onSubmit={handleSubmit}>
              <input name="name" value={form.name} onChange={handleChange} placeholder="Nombre del equipo" required />
              <input name="city" value={form.city} onChange={handleChange} placeholder="Ciudad" required />
              <input name="founded" type="number" value={form.founded} onChange={handleChange} placeholder="Año fundación" required />
              <button type="submit">Crear Equipo</button>
            </form>

            {teams.length === 0
              ? <p>No hay equipos aún.</p>
              : (
                <ul className="team-list">
                  {teams.map(team => (
                    <li key={team._id}>
                      <strong>{team.name}</strong> ({team.city}) – {team.founded}
                    </li>
                  ))}
                </ul>
              )
            }
          </div>
        } />
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
