import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.scss';


// Componente Login
function Login() {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:8000/api/users/login/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Error al iniciar sesión');
      }

      // Guardar en localStorage
      localStorage.setItem('user', JSON.stringify(data));
      window.location.href = '/dashboard';
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form-container">
        <h2>Iniciar Sesión</h2>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="username">Usuario</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Contraseña</label>
            <input
              type="password"
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </div>

          <button 
            type="submit" 
            className="auth-button"
            disabled={loading}
          >
            {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </button>
        </form>
        <div className="auth-link">
          <p>¿No tienes cuenta? <a href="/register">Regístrate</a></p>
        </div>
      </div>
    </div>
  );
}

// Componente Register
function Register() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    first_name: '',
    last_name: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:8000/api/users/register/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(Object.values(data).flat().join(' '));
      }

      setSuccess('¡Registro exitoso! Redirigiendo al login...');
      setTimeout(() => {
        window.location.href = '/login';
      }, 2000);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form-container">
        <h2>Crear Cuenta</h2>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="username">Usuario</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Contraseña</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="first_name">Nombre</label>
            <input
              type="text"
              id="first_name"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="last_name">Apellido</label>
            <input
              type="text"
              id="last_name"
              name="last_name"
              value={formData.last_name}
              onChange={handleChange}
              required
            />
          </div>

          <button 
            type="submit" 
            className="auth-button"
            disabled={loading}
          >
            {loading ? 'Registrando...' : 'Registrarse'}
          </button>
        </form>
        <div className="auth-link">
          <p>¿Ya tienes cuenta? <a href="/login">Iniciar Sesión</a></p>
        </div>
      </div>
    </div>
  );
}

// Actualización del componente Dashboard
function Dashboard() {
  const [teams, setTeams] = useState([]);
  const [form, setForm] = useState({ name: '', city: '', founded: '' });
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // Obtener usuario del localStorage
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }

    // Cargar equipos al inicio
    loadTeams();
  }, []);

// Reemplaza la función loadTeams con esta versión corregida
const loadTeams = async () => {
  try {
    const userData = JSON.parse(localStorage.getItem('user') || '{}');

    console.log("Token utilizado:", userData.token);

    const response = await fetch('http://localhost:8000/api/teams/', {
      headers: {
        'Authorization': `JWT ${userData.token || ''}`,
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    });

    // Log para depuración
    console.log("Status code:", response.status);
    console.log("Headers:", Object.fromEntries([...response.headers.entries()]));

    // Verifica primero si la respuesta es exitosa
    if (!response.ok) {
      // Clona la respuesta antes de intentar leerla como JSON o texto
      const clonedResponse = response.clone();

      try {
        // Intenta leer como JSON primero
        const errorData = await response.json();
        throw new Error(errorData.error || `Error ${response.status}: ${response.statusText}`);
      } catch (jsonError) {
        // Si falla al leer como JSON, usa la respuesta clonada para leer como texto
        try {
          const textError = await clonedResponse.text();
          throw new Error(`Error ${response.status}: ${textError.substring(0, 100)}...`);
        } catch (textError) {
          // Si ambos fallan, solo usa la información del status
          throw new Error(`Error ${response.status}: ${response.statusText}`);
        }
      }
    }

    // Si llegamos aquí, la respuesta es OK y podemos leerla como JSON
    const data = await response.json();
    console.log("Equipos cargados:", data);
    setTeams(Array.isArray(data) ? data : []);
  } catch (err) {
    console.error("Error cargando equipos:", err);
    setError("Error cargando los equipos: " + err.message);
  }
};

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Actualiza handleSubmit con el mismo patrón
  const handleSubmit = async e => {
    e.preventDefault();
    setError('');

    try {
      const userData = JSON.parse(localStorage.getItem('user') || '{}');
      const response = await fetch('http://localhost:8000/api/teams/create/', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `JWT ${userData.token || ''}`,
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          name: form.name,
          city: form.city,
          founded: parseInt(form.founded, 10)
        })
      });

      if (!response.ok) {
        // Clona la respuesta antes de intentar leerla como JSON o texto
        const clonedResponse = response.clone();

        try {
          // Intenta leer como JSON primero
          const errorData = await response.json();
          throw new Error(errorData.error || `Error ${response.status}: ${response.statusText}`);
        } catch (jsonError) {
          // Si falla al leer como JSON, usa la respuesta clonada para leer como texto
          try {
            const textError = await clonedResponse.text();
            throw new Error(`Error ${response.status}: ${textError.substring(0, 100)}...`);
          } catch (textError) {
            // Si ambos fallan, solo usa la información del status
            throw new Error(`Error ${response.status}: ${response.statusText}`);
          }
        }
      }

      const data = await response.json();
      setForm({ name: '', city: '', founded: '' });
      loadTeams(); // Recargar la lista de equipos
    } catch (err) {
      console.error("Error creando equipo:", err);
      setError("Error creando equipo: " + err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="app-container">
      <div className="header">
        <h1>Gestión de Equipos</h1>
        <div className="user-info">
          <span>Bienvenido, {user?.user?.username || 'Usuario'}</span>
          <button onClick={handleLogout} className="logout-btn">Cerrar Sesión</button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form className="team-form" onSubmit={handleSubmit}>
        <input name="name" value={form.name} onChange={handleChange} placeholder="Nombre del equipo" required />
        <input name="city" value={form.city} onChange={handleChange} placeholder="Ciudad" required />
        <input name="founded" type="number" value={form.founded} onChange={handleChange} placeholder="Año fundación" required />
        <button type="submit">Crear Equipo</button>
      </form>

      {teams.length === 0
        ? <p>No hay equipos aún.</p>
        : (
          <ul className="team-list">
            {teams.map(team => (
              <li key={team.id || team._id}>
                <strong>{team.name}</strong> ({team.city}) – {team.founded}
              </li>
            ))}
          </ul>
        )
      }
    </div>
  );
}

// Función para verificar si hay un usuario autenticado
const isAuthenticated = () => {
  return localStorage.getItem('user') !== null;
};

// Componente principal App
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={!isAuthenticated() ? <Login /> : <Navigate to="/dashboard" />} />
        <Route path="/register" element={!isAuthenticated() ? <Register /> : <Navigate to="/dashboard" />} />
        <Route path="/dashboard" element={isAuthenticated() ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/" element={<Navigate to={isAuthenticated() ? "/dashboard" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
