import React, { useState, useEffect } from 'react';
import { authHeader } from '../../services/auth';
import '../../styles/main.scss';

// Simulación de servicios API (reemplaza con tus llamadas reales)
const mockApiService = {
  getTasks: async () => {
    // Simula la obtención de tareas
    await new Promise(resolve => setTimeout(resolve, 500));
    // Intenta cargar desde localStorage o devuelve un array vacío
    const storedTasks = localStorage.getItem('myAppTasks');
    return storedTasks ? JSON.parse(storedTasks) : [];
  },
  createTask: async (taskData) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    const newTask = { ...taskData, id: Date.now().toString(), _id: Date.now().toString() }; // Simula ID de backend
    const tasks = await mockApiService.getTasks();
    const updatedTasks = [...tasks, newTask];
    localStorage.setItem('myAppTasks', JSON.stringify(updatedTasks));
    return newTask;
  },
  updateTask: async (taskId, taskData) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    let tasks = await mockApiService.getTasks();
    tasks = tasks.map(t => (t.id === taskId || t._id === taskId) ? { ...t, ...taskData, id: taskId } : t);
    localStorage.setItem('myAppTasks', JSON.stringify(tasks));
    return { ...taskData, id: taskId };
  },
  deleteTask: async (taskId) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    let tasks = await mockApiService.getTasks();
    tasks = tasks.filter(t => t.id !== taskId && t._id !== taskId);
    localStorage.setItem('myAppTasks', JSON.stringify(tasks));
    return true;
  }
};


function MyTasksSection() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({
    image: '',
    title: '',
    description: '',
    participants: '',
    duration: '',
    category: '',
    material: ''
  });
  const [loading, setLoading] = useState(false);
  const [formLoading, setFormLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedTask, setSelectedTask] = useState(null);

  const loadTasks = async () => {
    setLoading(true);
    setError('');
    try {
      const fetchedTasks = await mockApiService.getTasks(); // USA TU SERVICIO REAL AQUÍ
      setTasks(fetchedTasks);
    } catch (err) {
      setError(err.message || 'Error al cargar las tareas');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTasks();
  }, []);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setFormLoading(true);
    try {
      const newTask = await mockApiService.createTask(form); // USA TU SERVICIO REAL AQUÍ
      setTasks(prevTasks => [...prevTasks, newTask]);
      setForm({ // Reset form
        image: '', title: '', description: '', participants: '',
        duration: '', category: '', material: ''
      });
    } catch (err) {
      setError(err.message || 'Error al crear la tarea');
    } finally {
      setFormLoading(false);
    }
  };

  const openTaskModal = (task) => {
    setSelectedTask(task);
  };

  const closeTaskModal = () => {
    setSelectedTask(null);
  };

  const handleTaskUpdated = (updatedTask) => {
    setTasks(prevTasks => prevTasks.map(t => (t.id === updatedTask.id || t._id === updatedTask.id) ? updatedTask : t));
    // closeTaskModal(); // El modal se cierra desde dentro al guardar
  };

  const handleTaskDeleted = (taskId) => {
    setTasks(prevTasks => prevTasks.filter(t => t.id !== taskId && t._id !== taskId));
    // closeTaskModal(); // El modal se cierra desde dentro al eliminar
  };

  return (
    <div className="content-wrapper">
      <h2>Mis Tareas</h2>
      {error && <div className="error-message">{error}</div>}

      <div className="tasks-layout">
        <div className="tasks-form-column">
          <div className="card">
            <div className="card-header">
              <h2 className="card-title">Crear Nueva Tarea</h2>
            </div>
            <div className="card-body">
              <form className="task-form" onSubmit={handleSubmit}>
                <div className="form-control">
                  <label>URL de imagen</label>
                  <input
                    name="image"
                    type="url"
                    value={form.image}
                    onChange={handleChange}
                    placeholder="https://ejemplo.com/imagen.jpg"
                  />
                </div>
                <div className="form-control">
                  <label>Título</label>
                  <input
                    name="title"
                    value={form.title}
                    onChange={handleChange}
                    placeholder="Título de la tarea"
                    required
                  />
                </div>
                <div className="form-control">
                  <label>Descripción</label>
                  <textarea
                    name="description"
                    value={form.description}
                    onChange={handleChange}
                    placeholder="Descripción detallada..."
                    required
                  ></textarea>
                </div>
                <div className="form-row">
                  <div className="form-control half-width">
                    <label>Participantes</label>
                    <input
                      name="participants"
                      type="number"
                      value={form.participants}
                      onChange={handleChange}
                      placeholder="Número de jugadores"
                      required
                    />
                  </div>
                  <div className="form-control half-width">
                    <label>Duración (min)</label>
                    <input
                      name="duration"
                      type="number"
                      value={form.duration}
                      onChange={handleChange}
                      placeholder="Ej: 30"
                      required
                    />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-control half-width">
                    <label>Categoría</label>
                    <input
                      name="category"
                      value={form.category}
                      onChange={handleChange}
                      placeholder="Ej: Técnica, Táctica..."
                      required
                    />
                  </div>
                  <div className="form-control half-width">
                    <label>Material</label>
                    <input
                      name="material"
                      value={form.material}
                      onChange={handleChange}
                      placeholder="Material necesario"
                      required
                    />
                  </div>
                </div>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={formLoading}
                >
                  <i className={`fas ${formLoading ? 'fa-spinner fa-spin' : 'fa-plus'}`} style={{ marginRight: '8px' }}></i>
                  {formLoading ? 'Creando...' : 'Crear Tarea'}
                </button>
              </form>
            </div>
          </div>
        </div>

        <div className="tasks-list-column">
          <div className="card">
            <div className="card-header">
              <h2 className="card-title">Tareas Creadas ({tasks.length})</h2>
            </div>
            <div className="card-body">
              {loading && tasks.length === 0 ? (
                 <div className="loading">
                   <i className="fas fa-spinner fa-spin"></i> Cargando tareas...
                 </div>
              ) : tasks.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-state-icon">
                    <i className="fas fa-clipboard-list"></i>
                  </div>
                  <p className="empty-state-message">No hay tareas creadas aún</p>
                  <p>Utiliza el formulario para crear tu primera tarea.</p>
                </div>
              ) : (
                <div className="task-grid">
                  {tasks.map(task => (
                    <div
                      key={task.id || task._id}
                      className="task-card"
                      onClick={() => openTaskModal(task)}
                    >
                      {task.image && <img src={task.image} alt={task.title} onError={(e) => e.target.style.display='none'} />}
                      <h3>{task.title}</h3>
                      <p className="task-card-description">{task.description}</p>
                      <div className="task-properties">
                        <div className="task-property">
                          <i className="fas fa-users"></i> {task.participants}
                        </div>
                        <div className="task-property">
                          <i className="fas fa-clock"></i> {task.duration} min
                        </div>
                        <span className="task-category">{task.category}</span>
                      </div>
                      <div className="task-material">
                        <i className="fas fa-tools"></i> {task.material}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {selectedTask && (
        <TaskModal
          task={selectedTask}
          onClose={closeTaskModal}
          onTaskUpdated={handleTaskUpdated}
          onTaskDeleted={handleTaskDeleted}
        />
      )}
    </div>
  );
}

// Componente del Modal
function TaskModal({ task, onClose, onTaskUpdated, onTaskDeleted }) {
  const [isEditing, setIsEditing] = useState(false);
  const [modalLoading, setModalLoading] = useState(false);
  const [modalError, setModalError] = useState('');
  const [editForm, setEditForm] = useState({
    image: '', title: '', description: '',
    participants: '', duration: '', category: '', material: ''
  });

  useEffect(() => {
    if (task) {
      setEditForm({
        image: task.image || '',
        title: task.title || '',
        description: task.description || '',
        participants: task.participants || '',
        duration: task.duration || '',
        category: task.category || '',
        material: task.material || ''
      });
    }
    setIsEditing(false); // Siempre empieza en modo vista al abrir/cambiar tarea
    setModalError('');
  }, [task]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    setModalError('');
    setModalLoading(true);
    try {
      const updatedTaskData = await mockApiService.updateTask(task.id || task._id, editForm); // USA TU SERVICIO REAL AQUÍ
      onTaskUpdated(updatedTaskData);
      setIsEditing(false); // Vuelve a modo vista
    } catch (err) {
      setModalError(err.message || 'Error al guardar la tarea');
    } finally {
      setModalLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm(`¿Estás seguro de eliminar la tarea "${task.title}"?`)) {
      setModalError('');
      setModalLoading(true);
      try {
        await mockApiService.deleteTask(task.id || task._id); // USA TU SERVICIO REAL AQUÍ
        onTaskDeleted(task.id || task._id);
        onClose(); // Cierra el modal después de eliminar
      } catch (err) {
        setModalError(err.message || 'Error al eliminar la tarea');
      } finally {
        setModalLoading(false);
      }
    }
  };

  if (!task) return null;

  return (
    <div className="task-modal-overlay" onClick={onClose}>
      <div className="task-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="task-modal-header">
          <h2>
            {isEditing ? <><i className="fas fa-edit"></i> Editar Tarea</> : <><i className="fas fa-clipboard-check"></i> Detalles de la Tarea</>}
          </h2>
          <button className="task-modal-close-btn" onClick={onClose}>&times;</button>
        </div>

        <div className="task-modal-body">
          {modalError && <div className="error-message" style={{ marginBottom: '16px' }}>{modalError}</div>}

          {!isEditing ? (
            <div className="task-modal-view-details">
              {editForm.image && <img src={editForm.image} alt={editForm.title} className="task-image-display" onError={(e) => e.target.style.display='none'}/>}
              <h3 className="task-title-display">{editForm.title}</h3>
              <p className="task-description-display">{editForm.description}</p>
              
              <h4><i className="fas fa-info-circle" style={{marginRight: '8px', color: 'var(--primary-color)'}}></i>Detalles Adicionales:</h4>
              <div className="task-meta-grid">
                <div className="task-meta-item">
                  <i className="fas fa-users"></i><strong>Participantes:</strong> {editForm.participants}
                </div>
                <div className="task-meta-item">
                  <i className="fas fa-clock"></i><strong>Duración:</strong> {editForm.duration} min
                </div>
                <div className="task-meta-item">
                  <i className="fas fa-tags"></i><strong>Categoría:</strong> {editForm.category}
                </div>
                <div className="task-meta-item">
                  <i className="fas fa-tools"></i><strong>Material:</strong> {editForm.material}
                </div>
              </div>
            </div>
          ) : (
            <form className="task-modal-edit-form" onSubmit={(e) => { e.preventDefault(); handleSave(); }}>
              <div className="form-group">
                <label htmlFor="task-title-edit">Título</label>
                <input id="task-title-edit" name="title" value={editForm.title} onChange={handleChange} required />
              </div>
              <div className="form-group">
                <label htmlFor="task-image-edit">URL de Imagen</label>
                <input id="task-image-edit" name="image" type="url" value={editForm.image} onChange={handleChange} placeholder="https://ejemplo.com/imagen.jpg" />
              </div>
              <div className="form-group">
                <label htmlFor="task-description-edit">Descripción</label>
                <textarea id="task-description-edit" name="description" value={editForm.description} onChange={handleChange} required></textarea>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="task-participants-edit">Participantes</label>
                  <input id="task-participants-edit" name="participants" type="number" value={editForm.participants} onChange={handleChange} required />
                </div>
                <div className="form-group">
                  <label htmlFor="task-duration-edit">Duración (min)</label>
                  <input id="task-duration-edit" name="duration" type="number" value={editForm.duration} onChange={handleChange} required />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="task-category-edit">Categoría</label>
                  <input id="task-category-edit" name="category" value={editForm.category} onChange={handleChange} required />
                </div>
                <div className="form-group">
                  <label htmlFor="task-material-edit">Material</label>
                  <input id="task-material-edit" name="material" value={editForm.material} onChange={handleChange} required />
                </div>
              </div>
            </form>
          )}
        </div>

        <div className="task-modal-actions">
          <button className="btn btn-danger" onClick={handleDelete} disabled={modalLoading}>
            <i className={`fas ${modalLoading && isEditing ? 'fa-spinner fa-spin' : 'fa-trash-alt'}`}></i> Eliminar
          </button>
          {isEditing ? (
            <>
              <button className="btn btn-secondary" onClick={() => setIsEditing(false)} disabled={modalLoading}>
                <i className="fas fa-times"></i> Cancelar
              </button>
              <button className="btn btn-success" onClick={handleSave} disabled={modalLoading}>
                <i className={`fas ${modalLoading ? 'fa-spinner fa-spin' : 'fa-save'}`}></i> {modalLoading ? 'Guardando...' : 'Guardar Cambios'}
              </button>
            </>
          ) : (
            <button className="btn btn-primary" onClick={() => setIsEditing(true)}>
              <i className="fas fa-edit"></i> Editar Tarea
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default MyTasksSection;
